package com.syntax.class08;

public class MultiplicationTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//we want to create multiplication table 
		//if 1*1 =1
		// 1*2=2
		//1*3=3
		
		int num=3;
		for(int a=1; a<=10; a++) {
			System.out.println(num + " * "+ a +" = " +a*num);
		}
	}

}
