package com.syntax.class09;

public class PatternsMore {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int a=1; a<=5; a++) {
			for(int b=5; b>=1; b--) {
				System.out.print(b);
			}
			System.out.println();
		}
		//printing 555....444....333
		for(int m=5; m>=1; m--) {
			for(int n=5; n>=1; n--) {
				System.out.print(m);
			}
			System.out.println();
		}

	}

}
